mapper.select:
SELECT m.id, m.title, m.time, m.share_type AS shareType, m.drug_user_id AS drugUserId, m.drug_user_name AS drugUserName, count(m.title) AS peopleNumber, SUM(m.read_time) AS totalDuration, p.product_id AS productId, l.prod_name AS prodName, u.sale_type AS saleType FROM ( SELECT t.id, t.title, t.time, r.share_type, r.data_id, r.drug_user_id, r.drug_user_name, r.read_person_id, r.read_person_name, r.read_time FROM activity_info t LEFT JOIN activity_read r ON t.id = r.data_id JOIN activity_read_ip ip ON r.read_token = ip.read_token WHERE 1 = 1 AND t.id = r.data_id AND ( ip.isp IS NULL OR ip.isp != '阿里云' ) AND r.read_person_id != '' GROUP BY read_person_id, id ) m JOIN t_content_product p ON p.content_id = m.id JOIN product_line l ON p.product_id = l.prod_id JOIN drug_user u ON m.drug_user_id = u.id
WHERE 1 = 1
AND l.prod_id = #{contentSharingRequest.productId}
AND m.drug_user_id = #{contentSharingRequest.drugUserId}
AND m.share_type = #{contentSharingRequest.shareType}
AND m.time >= #{contentSharingRequest.startTimeBefore}
AND m.time <= #{contentSharingRequest.startTimeAfter}
GROUP BY title LIMIT #{contentSharingRequest.currentSize},#{contentSharingRequest.pageSize}

SELECT count(p.id) FROM ( SELECT m.id FROM ( SELECT t.id, t.title, t.time, r.share_type, r.data_id, r.drug_user_id, r.drug_user_name, r.read_person_id, r.read_person_name, r.read_time FROM activity_info t LEFT JOIN activity_read r ON t.id = r.data_id JOIN activity_read_ip ip ON r.read_token = ip.read_token WHERE 1 = 1 AND t.id = r.data_id AND ( ip.isp IS NULL OR ip.isp != '阿里云' ) AND r.read_person_id != '' GROUP BY read_person_id, id ) m JOIN t_content_product p ON p.content_id = m.id JOIN product_line l ON p.product_id = l.prod_id JOIN drug_user u ON m.drug_user_id = u.id
WHERE 1 = 1
AND l.prod_id = #{contentSharingRequest.productId}
AND m.drug_user_id = #{contentSharingRequest.drugUserId}
AND m.share_type = #{contentSharingRequest.shareType}
AND m.time >= #{contentSharingRequest.startTimeBefore}
AND m.time <= #{contentSharingRequest.startTimeAfter}
GROUP BY title ) p
