-- 电话覆盖 拜访登记覆盖 SELECT DISTINCT d.id hcpId, h.id hciId, DATE_FORMAT(b.create_time,'%Y-%m') timeStr FROM `virtual_doctor_call_info` a INNER JOIN doctor d ON a.virtual_doctor_id = d.id INNER JOIN virtual_doctor_call_info_mend b on a.id = b.call_id INNER JOIN hospital h on h.id = d.hospital_id WHERE b.is_recruit = 1 AND a.product_id = #{productId} AND (a.status_name = 'answer' or a.visit_channel != 1) AND DATE_FORMAT(a.create_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(a.create_time,'%Y-%m') <= #{endTime} UNION -- 文章阅读 SELECT DISTINCT d.id hcpId, h.id hciId, DATE_FORMAT(r.create_time,'%Y-%m') timeStr FROM t_content_product a INNER JOIN activity_read r ON r.data_id = a.content_id INNER JOIN doctor d ON r.read_person_id = d.id INNER JOIN hospital h on h.id = d.hospital_id LEFT JOIN activity_read_ip i ON i.read_token = r.read_token WHERE a.product_id = #{productId} AND DATE_FORMAT(r.create_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(r.create_time,'%Y-%m') <= #{endTime} AND (i.isp != '阿里云' OR i.isp IS NULL) AND d.id IN (
AND pd.product_id = #{productId} ) UNION -- 微信回复 SELECT DISTINCT d.id doctorId, h.id userId, DATE_FORMAT(b.message_time,'%Y-%m') total FROM drug_user_doctor a JOIN `virtual_message` b ON a.doctor_id = b.doctor_id AND a.drug_user_id = b.drug_user_id JOIN doctor d ON b.doctor_id = d.id JOIN hospital h on h.id = d.hospital_id WHERE a.is_available = 1 AND a.prod_id = #{productId} AND b.user_type = 2 and DATE_FORMAT(b.message_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.message_time,'%Y-%m') <= #{endTime} AND b.wechat_message_type NOT IN ('系统消息','撤回提醒') AND d.id IN (
AND pd.product_id = #{productId} ) UNION -- 会议覆盖 SELECT DISTINCT d.id doctorId, h.id userId, DATE_FORMAT(b.attend_start_time,'%Y-%m') total FROM t_meeting_data a INNER JOIN t_meeting_attend_details b ON a.id = b.meeting_id INNER JOIN drug_user_doctor c ON b.doctor_id = c.doctor_id and a.product_id = c.prod_id INNER JOIN virtual_doctor_call_info_mend pd on c.prod_id = pd.product_id and c.doctor_id = pd.virtual_doctor_id INNER JOIN doctor d ON b.doctor_id = d.id INNER JOIN hospital h on h.id = d.hospital_id WHERE pd.is_recruit = 1 AND c.prod_id = #{productId} AND c.is_available = 1 AND DATE_FORMAT(b.attend_start_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.attend_start_time,'%Y-%m') <= #{endTime}

SELECT d.id hcpId, DATE_FORMAT(b.message_time,'%Y-%m') timeStr FROM drug_user_doctor a
JOIN `virtual_message` b ON a.doctor_id = b.doctor_id AND a.drug_user_id = b.drug_user_id
JOIN doctor d ON b.doctor_id = d.id
JOIN hospital h on h.id = d.hospital_id
WHERE a.is_available = 1 AND a.prod_id = #{productId} AND b.user_type = 2
and DATE_FORMAT(b.message_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.message_time,'%Y-%m') <= #{endTime}
AND b.wechat_message_type NOT IN ('系统消息','撤回提醒') AND d.id IN (

AND pd.product_id = #{productId}
)

SELECT d.id hcpId, DATE_FORMAT(b.message_time,'%Y-%m') timeStr FROM drug_user_doctor a
JOIN `virtual_message` b ON a.doctor_id = b.doctor_id AND a.drug_user_id = b.drug_user_id
JOIN doctor d ON b.doctor_id = d.id
JOIN hospital h on h.id = d.hospital_id
WHERE a.is_available = 1 AND a.prod_id = #{productId} AND b.user_type = 1
and DATE_FORMAT(b.message_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.message_time,'%Y-%m') <= #{endTime}
AND b.wechat_message_type NOT IN ('系统消息','撤回提醒') AND d.id IN (

AND pd.product_id = #{productId}
)

SELECT DATE_FORMAT(a.start_time,'%Y-%m') timeStr, count(a.id) hciId FROM
t_meeting_data a
WHERE a.product_id = #{productId}
AND DATE_FORMAT(a.start_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(a.start_time,'%Y-%m') <= #{endTime}
GROUP BY timeStr

SELECT DISTINCT d.id hcpId, DATE_FORMAT(r.create_time,'%Y-%m') timeStr FROM
t_content_product a
INNER JOIN activity_read r ON r.data_id = a.content_id
INNER JOIN doctor d ON r.read_person_id = d.id
LEFT JOIN activity_read_ip i ON i.read_token = r.read_token
WHERE a.product_id = #{productId}
AND DATE_FORMAT(r.create_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(r.create_time,'%Y-%m') <= #{endTime}
AND (i.isp != '阿里云' OR i.isp IS NULL) AND d.id IN (
 AND pd.product_id = #{productId}
AND DATE_FORMAT(pd.create_time,'%Y-%m') <= #{endTime}
)
UNION
select DISTINCT d.id hcpId, DATE_FORMAT(b.create_time,'%Y-%m') timeStr FROM
t_content_product a
INNER JOIN wx_user_read b ON b.content_id = a.content_id
INNER JOIN wx_users c on b.user_id = c.id
INNER JOIN doctor d ON c.doctor_id = d.id
WHERE a.product_id = #{productId}
AND DATE_FORMAT(b.create_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.create_time,'%Y-%m') <= #{endTime}
AND d.id IN (
 AND pd.product_id = #{productId}
AND DATE_FORMAT(pd.create_time,'%Y-%m') <= #{endTime}
)

select DISTINCT d.id hcpId, DATE_FORMAT(b.create_time,'%Y-%m') timeStr from
t_content_product a
inner join activity_share b on a.content_id = b.data_id
inner join doctor d on b.share_person_id = d.id
WHERE
a.product_id = #{productId}
AND DATE_FORMAT(b.create_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.create_time,'%Y-%m') <= #{endTime}
AND d.id IN (
select distinct pd.virtual_doctor_id from virtual_doctor_call_info_mend pd where pd.product_id = #{productId}
and  DATE_FORMAT(pd.create_time,'%Y-%m') <= #{endTime} and pd.virtual_doctor_id > 0 and pd.is_recruit = 1
)
union
select DISTINCT d.id hcpId, DATE_FORMAT(b.create_time,'%Y-%m') timeStr from
drug_user_doctor a
inner join virtual_message b on a.doctor_id = b.doctor_id
inner join doctor d on b.doctor_id = d.id
WHERE a.prod_id = #{productId} and a.is_available = 1 and b.user_type = 1 and (b.wechat_message_type
in ('链接/小程序/聊天记录', '网页') or (b.message like 'http%' and b.message like '%token=%'))
AND DATE_FORMAT(b.message_time,'%Y-%m') >= #{startTime} AND DATE_FORMAT(b.message_time,'%Y-%m') <= #{endTime}
AND d.id IN (
select distinct pd.virtual_doctor_id from virtual_doctor_call_info_mend pd where pd.product_id = #{productId}
and DATE_FORMAT(pd.create_time,'%Y-%m') <= #{endTime} and pd.virtual_doctor_id > 0 and pd.is_recruit = 1
)
