UNION
SELECT DISTINCT d.id FROM virtual_message vm INNER JOIN doctor d ON vm.doctor_id = d.id INNER JOIN drug_user_doctor dud ON d.id = dud.doctor_id INNER JOIN drug_user du ON dud.drug_user_id = du.id WHERE dud.is_available = 1 AND vm.user_type = 2
AND dud.prod_id IN
#{productId}
AND du.id IN
#{drugUserId}
AND vm.message_time BETWEEN #{startTime} AND #{endTime}
GROUP BY d.id HAVING COUNT(*) >=3
UNION SELECT DISTINCT d.id FROM doctor d INNER JOIN drug_user_doctor dud ON d.id = dud.doctor_id INNER JOIN drug_user du ON dud.drug_user_id = du.id INNER JOIN t_meeting_attend_details tmad ON d.id = tmad.doctor_id INNER JOIN t_meeting_data tmd ON tmad.meeting_id = tmd.id WHERE 1=1
AND tmad.attend_start_time BETWEEN #{startTime} AND #{endTime}
AND dud.prod_id IN
#{productId}
AND du.id IN
#{drugUserId}
AND tmd.product_id IN
#{productId}
GROUP BY tmad.doctor_id HAVING SUM(tmad.attend_sum_time) >= 10
) t

