SELECT v.visit_channel visitChannel, ( CASE WHEN v.visit_channel IS NULL THEN '未知' WHEN v.visit_channel=1 THEN '线上电话' WHEN v.visit_channel=2 THEN '微信' WHEN v.visit_channel=3 THEN '短信' WHEN v.visit_channel=4 THEN '邮件' WHEN v.visit_channel=5 THEN '面谈' WHEN v.visit_channel=6 THEN '内容分享' WHEN v.visit_channel=7 THEN '线下电话' END ) AS visitChannelStr, COUNT(DISTINCT d.id) doctorNum
FROM virtual_doctor_call_info v INNER JOIN doctor d ON v.virtual_doctor_id=d.id WHERE 1=1
AND v.virtual_drug_user_id IN
#{drugUserId}
AND v.product_id IN
#{productId}
AND v.create_time BETWEEN #{startTime} AND #{endTime}
GROUP BY v.visit_channel

