SELECT IFNULL(COUNT(*), 0) FROM ( SELECT DISTINCT d.id FROM activity_read ar INNER JOIN doctor d ON ar.read_person_id=d.id LEFT JOIN activity_read_ip ari ON ar.read_token = ari.read_token WHERE
( ari.isp IS NULL OR ari.isp != '阿里云' )
AND ar.drug_user_id IN
#{drugUserId}
AND ar.data_id IN ( SELECT content_id FROM t_content_product WHERE product_id IN
#{productId}
)
AND ar.create_time BETWEEN #{startTime} AND #{endTime}
UNION
SELECT DISTINCT d.id FROM wx_users wu INNER JOIN doctor d ON wu.doctor_id = d.id INNER JOIN wx_user_read wur ON wu.id = wur.user_id INNER JOIN drug_user_doctor dud ON d.id = dud.doctor_id INNER JOIN drug_user du ON dud.drug_user_id = du.id WHERE dud.is_available = 1
AND du.id IN
#{drugUserId}
AND wur.content_id IN ( SELECT content_id FROM t_content_product WHERE product_id IN
#{productId}
)
AND wur.create_time BETWEEN #{startTime} AND #{endTime}
) t

