SELECT DISTINCT d.id doctorId, DATE_FORMAT(wur.create_time, '%Y-%m-%d') visitDate FROM wx_users wu INNER JOIN doctor d ON wu.doctor_id = d.id INNER JOIN wx_user_read wur ON wu.id = wur.user_id INNER JOIN drug_user_doctor dud ON d.id = dud.doctor_id INNER JOIN drug_user du ON dud.drug_user_id = du.id WHERE dud.is_available = 1
AND du.id IN
