mapper.select:
SELECT doctor_id FROM (
SELECT id doctor_id FROM doctor WHERE telephone=#{mobile}
UNION
SELECT doctor_id FROM doctor_telephone WHERE telephone=#{mobile}
) t
LIMIT 1

SELECT COUNT(DISTINCT d.id) FROM doctor d LEFT JOIN hospital h ON d.hospital_id=h.id LEFT JOIN doctor_mend dm ON d.id=dm.virtual_doctor_id JOIN (
SELECT doctor_id,min(DATE_FORMAT(modify_time,'%Y-%m-%d %H:%i:%s')) doctorCreateTime, IF(drug_user_id=#{drugUserId},1,0) click FROM drug_user_doctor WHERE is_available=1
AND drug_user_id IN
#{virtualDrugUserId}
AND prod_id IN
#{productLineId}
GROUP BY doctor_id ) temp ON d.id=temp.doctor_id
LEFT JOIN ( SELECT c.virtual_doctor_id, c.create_time, m.visit_result, m.next_visit_time FROM virtual_doctor_call_info c LEFT JOIN virtual_doctor_call_info_mend m ON m.call_id=c.id WHERE c.id IN ( SELECT MAX(id) FROM virtual_doctor_call_info WHERE virtual_drug_user_id IN
#{virtualDrugUserId}
AND product_id IN
#{productLineId}
GROUP BY virtual_doctor_id )
) tel_visit ON tel_visit.virtual_doctor_id=d.id
INNER JOIN ( SELECT v.virtual_doctor_id, DATE_FORMAT( max(v.create_time), '%Y-%m-%d' ) maxTime, DATE_FORMAT( max(m.next_visit_time), '%Y-%m-%d' ) nextTime, DATEDIFF(NOW(),max(m.next_visit_time)) as overtime
FROM virtual_doctor_call_info v LEFT JOIN virtual_doctor_call_info_mend m ON v.id = m.call_id WHERE v.virtual_drug_user_id IN
#{virtualDrugUserId}
GROUP BY v.virtual_doctor_id HAVING nextTime >= maxTime AND nextTime <= DATE_FORMAT(NOW(), '%Y-%m-%d') ) st1 ON temp.doctor_id=st1.virtual_doctor_id
INNER JOIN (
SELECT #{v.doctorId} 'doctorId', #{v.visitIntervalDay} 'visitIntervalDay'
UNION SELECT #{v.doctorId} 'doctorId', #{v.visitIntervalDay} 'visitIntervalDay'
) st2 ON temp.doctor_id=st2.doctorId
INNER JOIN (
SELECT tmad.doctor_id doctorId, round( ( UNIX_TIMESTAMP(m.start_time) - UNIX_TIMESTAMP(NOW()) ) / 60 ) AS overMinute FROM t_meeting_attend_details tmad INNER JOIN ( SELECT id, tma.start_time FROM t_meeting_data tma WHERE DATE_FORMAT(tma.start_time, '%Y-%m-%d') >= DATE_FORMAT(NOW(), '%Y-%m-%d') AND tma.product_id ) m ON tmad.meeting_id = m.id LEFT JOIN virtual_meeting_no_remind r ON m.id = r.meeting_id WHERE r.id IS NULL HAVING overMinute >=0 AND overMinute <= ( SELECT min(
before_meeting_frequency * 60 ) FROM virtual_product_visit_frequency WHERE product_id IN
#{productLineId}
)
) st5 ON temp.doctor_id=st5.doctorId
INNER JOIN ( SELECT round( ( UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(tmd.end_time) ) / 60 ) AS overMinute, MAX(tmd.end_time) asMaxEndTime, tmad.doctor_id doctorId FROM t_meeting_data tmd INNER JOIN t_meeting_attend_details tmad ON tmd.id = tmad.meeting_id INNER JOIN drug_user_doctor dud ON tmad.doctor_id = dud.doctor_id AND is_available = 1 WHERE dud.drug_user_id IN
#{virtualDrugUserId}
AND dud.prod_id IN
#{productLineId}
GROUP BY tmad.doctor_id HAVING overMinute <= ( SELECT min( after_meeting_frequency * 60 ) FROM virtual_product_visit_frequency WHERE product_id IN
#{productLineId}
) AND tmad.doctor_id NOT IN ( SELECT DISTINCT virtual_doctor_id FROM virtual_doctor_call_info WHERE virtual_drug_user_id IN
#{virtualDrugUserId}
AND
product_id IN
#{productLineId}
AND create_time >= MAX(tmd.end_time) )
) st6 ON temp.doctor_id=st6.doctorId
WHERE 1=1
AND ( d.name LIKE "%"#{search,jdbcType=VARCHAR}"%" OR d.hospital_name LIKE "%"#{search,jdbcType=VARCHAR}"%" OR d.telephone LIKE "%"#{search,jdbcType=VARCHAR}"%" OR d.depart LIKE "%"#{search,jdbcType=VARCHAR}"%" OR d.id IN ( SELECT doctor_id FROM doctor_telephone WHERE telephone LIKE "%"#{search,jdbcType=VARCHAR}"%" ) )
AND d.sex=#{sex}
AND d.email LIKE CONCAT('%', #{email}, '%')
AND d.positions LIKE CONCAT('%', #{title}, '%')
AND d.depart LIKE CONCAT('%', #{depart}, '%')
AND dm.wechat LIKE CONCAT('%', #{wechat}, '%')
AND d.id IN (SELECT DISTINCT virtual_doctor_id FROM doctor_mend WHERE address LIKE CONCAT('%', #{address}, '%'))
AND d.id IN ( SELECT doctor_id FROM drug_user_doctor_one_to_one WHERE is_add_wechat = 1 AND drug_user_id IN
#{virtualDrugUserId}
)
AND d.id IN ( SELECT doctor_id FROM drug_user_doctor_one_to_one WHERE (is_add_wechat IS NULL OR is_add_wechat=0) AND drug_user_id IN
#{virtualDrugUserId}
)
AND DATE_FORMAT(temp.doctorCreateTime, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startDoctorCreateTime}, '%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(temp.doctorCreateTime, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endDoctorCreateTime}, '%Y-%m-%d %H:%i:%s')
AND h.id=#{hospitalId}
AND h.province=#{hospitalProvince}
AND h.city LIKE #{hospitalCity}
AND h.level=#{hospitalGrade}
AND d.id IN (SELECT DISTINCT doctor_id FROM drug_user_doctor_quate WHERE is_has_drug=#{hasDrug}
AND drug_user_id IN
#{virtualDrugUserId}
AND product_id IN
#{productLineId}
)
AND d.id IN (SELECT DISTINCT doctor_id FROM drug_user_doctor_quate WHERE is_target=#{target}
AND drug_user_id IN
#{virtualDrugUserId}
AND product_id IN
#{productLineId}
)
AND d.id IN (SELECT DISTINCT doctor_id FROM drug_user_doctor_quate WHERE is_recruit=#{recruit}
AND drug_user_id IN
#{virtualDrugUserId}
AND product_id IN
#{productLineId}
)
AND d.id IN (SELECT DISTINCT doctor_id FROM drug_user_doctor_quate WHERE is_break_off=#{breakOff}
AND drug_user_id IN
#{virtualDrugUserId}
AND product_id IN
#{productLineId}
)
AND DATE_FORMAT(tel_visit.create_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startLastVisitTime}, '%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(tel_visit.create_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endLastVisitTime}, '%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(tel_visit.next_visit_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startNextVisitTime}, '%Y-%m-%d %H:%i:%s')
AND DATE_FORMAT(tel_visit.next_visit_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endNextVisitTime}, '%Y-%m-%d %H:%i:%s')
AND d.id IN ( SELECT DISTINCT q.doctor_id FROM drug_user_doctor_quate q INNER JOIN drug_user_doctor_quate_result r ON q.id=r.quate_id WHERE
q.product_id IN
#{productLineId}
AND q.drug_user_id IN
#{virtualDrugUserId}
AND r.result_id IN
#{resultId}
)
AND d.id IN (
SELECT DISTINCT doctor_id FROM virtual_doctor_dynamic_field_value WHERE 1=1 AND dynamic_field_id=#{v.dynamicFieldId} AND (
dynamic_field_value LIKE CONCAT('%', #{v2}, '%')
) )

