#{productLineId}
) as frequency FROM t_meeting_attend_details tmad INNER JOIN ( SELECT id, tma.start_time FROM t_meeting_data tma WHERE DATE_FORMAT(tma.start_time, '%Y-%m-%d') >= DATE_FORMAT(NOW(), '%Y-%m-%d') AND tma.product_id ) m ON tmad.meeting_id = m.id LEFT JOIN virtual_meeting_no_remind r ON m.id = r.meeting_id WHERE r.id IS NULL HAVING overMinute >=0 AND overMinute <=( SELECT min( before_meeting_frequency * 24 * 60 ) FROM virtual_product_visit_frequency WHERE product_id IN
#{productLineId}
)

