mapper.select:
select distinct vddfv.dynamic_field_id fieldId, vddfv.dynamic_field_name field, vddfv.dynamic_field_value value from virtual_doctor_dynamic_field_value vddfv
inner join virtual_doctor_dynamic_field vddf
on vddfv.dynamic_field_id=vddf.id
where vddf.classification=#{classification}
and vddfv.doctor_id=#{doctorId}

mapper.delete:
delete from virtual_doctor_dynamic_field_value where doctor_id=#{doctorId}
and dynamic_field_id in
(select id from virtual_doctor_dynamic_field where classification=#{classification})

mapper.select:
SELECT t.id doctorId, t1.prop, t1.lable, t2.dynamic_field_value value
FROM doctor t LEFT JOIN ( SELECT id fieldId, field_name lable, CONCAT("field",(@i :=@i + 1)) prop FROM virtual_doctor_dynamic_field, (SELECT @i := 0) a WHERE product_id = #{productId} OR classification IN (1,5) ORDER BY id ) t1 ON 1 = 1 LEFT JOIN ( SELECT t.id fieldId, t.`field_name`, t1.`doctor_id`, t1.`dynamic_field_value` FROM `virtual_doctor_dynamic_field` t LEFT JOIN `virtual_doctor_dynamic_field_value` t1 ON t.`id` = t1.`dynamic_field_id` WHERE product_id = #{productId} OR t.classification IN (1,5) ) t2 ON t.`id` = t2.doctor_id AND t1.fieldId = t2.fieldId WHERE t.id IN
#{doctorId}

SELECT t2.callId, t1.prop, t1.lable, t2.dynamic_field_value VALUE FROM ( SELECT id fieldId, field_name lable, CONCAT("field",(@i :=@i + 1)) prop FROM virtual_doctor_dynamic_field, (SELECT @i := 0) a WHERE product_id = #{productId} OR classification IN (1,5) ORDER BY id ) t1 INNER JOIN ( SELECT t.id fieldId, t.`field_name`, t1.`doctor_id`, t1.`dynamic_field_value`, t1.call_id callId FROM `virtual_doctor_dynamic_field` t LEFT JOIN `virtual_doctor_call_dynamic_field_value` t1 ON t.`id` = t1.`dynamic_field_id` WHERE (t.product_id = #{productId} OR t.classification IN (1,5)) AND t1.call_id IN
#{callId}
) t2 ON t1.fieldId = t2.fieldId

SELECT hcp_potential potential, is_has_drug hasDrug, is_target target, is_recruit recruit, is_break_off breakOff
 FROM drug_user_doctor_quate
 WHERE product_id=#{productId}
 AND doctor_id=#{doctorId}
 ORDER BY create_time DESC LIMIT 1
