mapper.select:
select a.doctor_id doctorId, (select name from doctor where id = a.doctor_id) nickname a.maxMessageTime lastTime, a.type messageType from ( select t.doctor_id,max(message_time) as maxMessageTime,t.type from ( select vm.doctor_id,vm.message,vm.message_time, 1 type from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where du.leader_path like #{leaderPath} and vm.message_type=1 union select vm.doctor_id,vm.message,vm.message_time, 2 type from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId} and vm.productId=#{productId}
and du.leader_path like #{leaderPath} and vm.message_type=2 union select vei.doctor_id,vei.content message,vei.create_time message_time, 3 type from virtual_email_info vei inner join doctor d on vei.doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and vei.productId=#{productId} and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} ) t group by t.type,t.doctor_id ) a where 1=1
and date_format(a.maxMessageTime,'%Y-%m-%d') >=#{startTime}
and date_format(a.maxMessageTime,'%Y-%m-%d') <=#{endTime}
and a.type=#{messageType}
order by a.maxMessageTime desc limit #{page},#{pageSize}

select count(1) from ( select t.doctor_id,max(message_time) as maxMessageTime,t.type from ( select vm.doctor_id,vm.message,vm.message_time, 1 type from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where du.leader_path like #{leaderPath} and vm.message_type=1 union select vm.doctor_id,vm.message,vm.message_time, 2 type from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId} and vm.productId=#{productId}
and du.leader_path like #{leaderPath} and vm.message_type=2 union select vei.doctor_id,vei.content message,vei.create_time message_time, 3 type from virtual_email_info vei inner join doctor d on vei.doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and vei.productId=#{productId} and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} ) t group by t.type,t.doctor_id ) a where 1=1
and date_format(a.maxMessageTime,'%Y-%m-%d') >=#{startTime}
and date_format(a.maxMessageTime,'%Y-%m-%d') <=#{endTime}
and a.type=#{messageType}

select vm.id id, vm.user_id userId, vm.user_type userType, vm.nickname nickname, vm.wechat_number wechatNumber, vm.telephone telephone, vm.wechat_message_status wechatMessageStatus, vm.wechat_message_type wechatMessageType, vm.message message, vm.message_type messageType, vm.message_time messageTime from virtual_message vm where 1=1
and vm.message_type = #{messageType}
and vm.message_type = #{messageType}
and vm.product_id=#{productId}
and vm.doctor_id=#{doctorId}
and date_format(vm.message_time,'%Y-%m-%d') between #{startTime} and #{endTime}
and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath}) order by vm.message_time desc limit #{page},#{pageSize}

select
count(1)
from virtual_message vm
where 1=1

    and vm.message_type = #{messageType}



    and vm.message_type = #{messageType}

        and vm.product_id=#{productId}



and vm.doctor_id=#{doctorId}

    and date_format(vm.message_time,'%Y-%m-%d') between #{startTime} and #{endTime}

and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath})

select vei.id, vei.doctor_id doctorId, (select name from doctor d where d.id =vei.doctor_id) nickname, vei.title, vei.content message, 3 messageType, vei.create_time messageTime from virtual_email_info vei inner join doctor d on vei.doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and vei.product_id=#{productId} and dud.prod_id=#{productId}
and date_format(vei.createTime,'%Y-%m-%d') >= #{startTime}
and date_format(vei.createTime,'%Y-%m-%d') <= #{endTime}
and du.leader_path like #{leaderPath} order by vei.create_time desc limit #{page}, #{pageSize}

select count(distinct vei.id)
from virtual_email_info vei
inner join doctor d
on vei.doctor_id=d.id
inner join drug_user_doctor dud
on d.id=dud.doctor_id
inner join drug_user du
on dud.drug_user_id=du.id
where 1=1

    and vei.product_id=#{productId}
    and dud.prod_id=#{productId}



    and date_format(vei.createTime,'%Y-%m-%d') >= #{startTime}



    and date_format(vei.createTime,'%Y-%m-%d') <= #{endTime}


and du.leader_path like #{leaderPath}

select
count(distinct vm.doctor_id)
from virtual_message vm
where 1=1

    and vm.message_type = #{messageType}



    and vm.message_type = #{messageType}

        and vm.product_id=#{productId}


and date_format(vm.message_time, '%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and vm.doctor_id in (
        select d.id from doctor d
        inner join drug_user_doctor dud
        on d.id=dud.doctor_id
        inner join drug_user du
        on dud.drug_user_id=du.id
        where 1=1


            dud.prod_id=#{productId}

        and du.leader_path like #{leaderPath}
    )
and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath})
