mapper.select:
select (select count(distinct vm.doctor_id) from virtual_message vm where vm.message_type=1 and vm.user_type=2 and date_format(vm.message_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d') and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath}) and vm.doctor_id in (select d.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} ) ) as wechatSum, ( select count(distinct vm.id) from virtual_message vm where vm.message_type=1 and vm.user_type=2 and date_format(vm.message_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d') and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath}) and vm.doctor_id in (select d.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} ) ) as wechatCount, ( select count(distinct vm.doctor_id) from virtual_message vm where vm.message_type=2 and vm.user_type=2
and vm.product_id = #{productId}
and date_format(vm.message_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d') and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath}) and vm.doctor_id in (select d.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} )
) as imSum, ( select count(distinct vm.id) from virtual_message vm where vm.message_type=2 and vm.user_type=2
and vm.product_id = #{productId}
and date_format(vm.message_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d') and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath}) and vm.doctor_id in (select d.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} ) ) as imCount

select a.doctor_id doctorId, a.doctorName nickname, a.maxMessageTime lastTime, a.type messageType, a.product_id productId from ( select t.doctor_id,max(message_time) as maxMessageTime,t.type,t.doctorName,t.product_id from ( select vm.doctor_id,vm.message,vm.message_time, 1 type, d.name doctorName, vm.product_id from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where du.leader_path like #{leaderPath} and vm.message_type=1 union select vm.doctor_id,vm.message,vm.message_time, 2 type, d.name doctorName, vm.product_id from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id inner join product_line pl on dud.prod_id=pl.prod_id where 1=1
and dud.prod_id=#{productId} and vm.product_id=#{productId}
and du.leader_path like #{leaderPath} and vm.message_type=2 group by vm.product_id union select vei.doctor_id,vei.content message,vei.create_time message_time, 3 type, d.name doctorName,vei.product_id from virtual_email_info vei inner join doctor d on vei.doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id inner join product_line pl on vei.product_id=pl.prod_id where 1=1
and vei.product_id=#{productId} and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} group by vei.product_id ) t group by t.type,t.doctor_id ) a where 1=1
and date_format(a.maxMessageTime,'%Y-%m-%d %H:%i:%s') >=#{startTime}
and date_format(a.maxMessageTime,'%Y-%m-%d %H:%i:%s') <=#{endTime}
and a.type=#{messageType}
and a.doctorName like concat('%',#{nickname},'%')
order by a.maxMessageTime desc limit #{page},#{pageSize} /* order by a.maxMessageTime desc limit 2,2*/

select count(1) from ( select t.doctor_id,max(message_time) as maxMessageTime,t.type,t.doctorName,t.product_id from ( select vm.doctor_id,vm.message,vm.message_time, 1 type, d.name doctorName, vm.product_id from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where du.leader_path like #{leaderPath} and vm.message_type=1 union select vm.doctor_id,vm.message,vm.message_time, 2 type, d.name doctorName, vm.product_id from virtual_message vm inner join doctor d on vm.doctor_id =d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id inner join product_line pl on dud.prod_id=pl.prod_id where 1=1
and dud.prod_id=#{productId} and vm.product_id=#{productId}
and du.leader_path like #{leaderPath} and vm.message_type=2 group by vm.product_id union select vei.doctor_id,vei.content message,vei.create_time message_time, 3 type, d.name doctorName,vei.product_id from virtual_email_info vei inner join doctor d on vei.doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id inner join product_line pl on vei.product_id=pl.prod_id where 1=1
and vei.product_id=#{productId} and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} group by vei.product_id ) t group by t.type,t.doctor_id ) a where 1=1
and date_format(a.maxMessageTime,'%Y-%m-%d %H:%i:%s') >=#{startTime}
and date_format(a.maxMessageTime,'%Y-%m-%d %H:%i:%s') <=#{endTime}
and a.type=#{messageType}
and a.doctorName like concat('%',#{nickname},'%')

select
vm.id id,
vm.user_id userId,
vm.user_type userType,
vm.nickname nickname,
vm.wechat_number wechatNumber,
vm.telephone telephone,
vm.wechat_message_status wechatMessageStatus,
vm.wechat_message_type wechatMessageType,
vm.message message,
vm.message_type messageType,
vm.message_time messageTime
from virtual_message vm
where 1=1

    and vm.message_type = #{messageType}



    and vm.message_type = #{messageType}

        and vm.product_id=#{productId}




and vm.doctor_id=#{doctorId}



    and date_format(vm.message_time,'%Y-%m-%d %H:%i:%s') >=#{startTime}



    and date_format(vm.message_time,'%Y-%m-%d %H:%i:%s') <=#{endTime}

and vm.drug_user_id in (select id from drug_user where leader_path like #{leaderPath})
order by vm.message_time desc limit #{page},#{pageSize}

