mapper.select:
SELECT id, field_value fieldValue, field_type fieldType,is_required required, is_supplement supplement
FROM virtual_product_classification
WHERE product_id=#{productId}

SELECT DISTINCT classification_id
FROM virtual_doctor_classification
WHERE product_id=#{productId}
AND doctor_id=#{doctorId}

mapper.delete:
DELETE
FROM virtual_doctor_classification
WHERE product_id=#{productId}
AND doctor_id=#{doctorId}
