mapper.select:
select distinct pl.prod_id productId, pl.prod_name productName from product_line pl inner join product_user pu on pu.prod_id=pl.prod_id join drug_user du on du.id=pu.user_id
INNER JOIN drug_user_doctor dud ON dud.prod_id=pl.prod_id AND dud.drug_user_id=du.id AND dud.is_available=1 AND dud.doctor_id=#{doctorId}
where du.leader_path like #{leaderPath} group by pl.prod_id

SELECT
    DATE_FORMAT(t.create_time, '%Y-%c-%e') existDate, count(t.create_time) dayCount
FROM
    virtual_doctor_call_info t
WHERE
    t.visit_channel = 2
AND t.virtual_drug_user_id = #{drugUserId}
AND t.virtual_doctor_id = #{doctorId}
AND t.product_id = #{productId}
AND t.create_time > date_sub(NOW(), INTERVAL #{dayNum} DAY)
GROUP BY
    existDate;
