select t.sum_call_time callSumTime,t.sum_call_num callNum,round((t.sum_call_time/if(t.sum_call_count=0,1,t.sum_call_count))) avgCallTime,t.drugUserId,t.drugUserName from ( select ifnull(sum(vdci.call_time),0) as sum_call_time,ifnull(count(distinct vdci.virtual_doctor_id),0) as sum_call_num, if(ifnull(count(distinct vdci.id),0)=0,1,ifnull(count(distinct vdci.id),0)) as sum_call_count,du.id drugUserId, du.name drugUserName from virtual_doctor_call_info vdci inner join virtual_doctor_call_info_details vdcid on vdci.id=vdcid.call_id inner join drug_user du on vdci.virtual_drug_user_id = du.id where 1=1
and vdci.product_id=#{productId}
and vdcid.status_name='answer'
and vdci.virtual_doctor_id in ( select d.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1
and dud.prod_id=#{productId}
and du.leader_path like #{leaderPath} ) and du.leader_path like #{leaderPath} group by vdci.virtual_drug_user_id order by sum_call_time desc limit 5 ) t

select distinct t.num, t.drugUserName from (
select ifnull(count(vei.id),0) as num,du.name as drugUserName
from virtual_email_info vei
inner join drug_user du
on vei.drug_user_id=du.id
where vei.product_id= #{productId}
and du.leader_path like #{leaderPath}




        and date_format(vei.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')


        and WEEKOFYEAR(vei.create_time)=WEEKOFYEAR(NOW())


        and MONTH(vei.create_time)=MONTH(NOW())


        and QUARTER(vei.create_time)=QUARTER(NOW())




and vei.doctor_id in (
    select distinct d.id from doctor d
    inner join drug_user_doctor dud
    on d.id=dud.doctor_id
  inner join drug_user du
  on dud.drug_user_id= du.id
  where dud.prod_id=#{productId}
  and du.leader_path like #{leaderPath}
)
group by vei.drug_user_id
) t  where t.num=#{minEmailCount}

select distinct t.num, t.drugUserName from (
select ifnull(count(vei.doctor_id),0) as num,du.name as drugUserName
        from virtual_email_info vei
        inner join drug_user du
        on vei.drug_user_id=du.id
        where vei.product_id= #{productId}
        and du.leader_path like #{leaderPath}




                and date_format(vei.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')


                and WEEKOFYEAR(vei.create_time)=WEEKOFYEAR(NOW())


                and MONTH(vei.create_time)=MONTH(NOW())


                and QUARTER(vei.create_time)=QUARTER(NOW())




        and vei.doctor_id in (
        select distinct d.id from doctor d
        inner join drug_user_doctor dud
        on d.id=dud.doctor_id
        inner join drug_user du
        on dud.drug_user_id= du.id
        where dud.prod_id=#{productId}
        and du.leader_path like #{leaderPath}
        )
        group by vei.drug_user_id

) t where t.num = #{minEmailCoveredCount}

select distinct t.drugUserName from (
select ifnull(count(vei.doctor_id),0) as num,du.name as drugUserName
from virtual_email_info vei
inner join drug_user du
on vei.drug_user_id=du.id
where vei.product_id= #{productId}
and du.leader_path like #{leaderPath}



        and date_format(vei.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')


        and WEEKOFYEAR(vei.create_time)=WEEKOFYEAR(NOW())


        and MONTH(vei.create_time)=MONTH(NOW())


        and QUARTER(vei.create_time)=QUARTER(NOW())



and vei.doctor_id in (
select distinct d.id from doctor d
inner join drug_user_doctor dud
on d.id=dud.doctor_id
inner join drug_user du
on dud.drug_user_id= du.id
where dud.prod_id=#{productId}
and du.leader_path like #{leaderPath}
)
group by vei.drug_user_id

) t where t.num < #{emailReach}

select distinct b3.num,b3.drugUserName from ( select count(distinct b.doctorId) num,b.drugUserName from( select distinct d.id doctorId,du.id drugUserId,du.name drugUserName from doctor d inner join doctor_virtual dv on d.id=dv.doctor_id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id and dud.prod_id=#{productId} and du.leader_path like #{leaderPath} and dv.client_level=#{level} ) b inner join
( select a.doctor_id,(to_days(now())-to_days(date_format(a.create_time,'%Y-%m-%d'))) as diff_days from ( select distinct t.doctor_id,max(t.create_time) as create_time from ( select distinct vdci.virtual_doctor_id doctor_id,max(vdci.create_time) as create_time from virtual_doctor_call_info vdci inner join virtual_doctor_call_info_details vdcid on vdci.id=vdcid.call_id inner join doctor d on vdci.virtual_doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where 1=1 and vdcid.status_name='answer' and du.leader_path like #{leaderPath} and dud.prod_id=#{productId} and vdci.product_id=#{productId} group by vdci.virtual_doctor_id
union
select distinct ar.read_person_id doctor_id, max(ar.create_time) as create_time from activity_read ar inner join t_content_product tcp on ar.data_id=tcp.content_id inner join activity_info ai on tcp.content_id=ai.id inner join doctor d on ar.read_person_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where du.leader_path like #{leaderPath} and dud.prod_id=#{productId} and tcp.product_id=#{productId}
group by ar.read_person_id ) t ) a where to_days(now())-to_days(date_format(a.create_time,'%Y-%m-%d')) >= #{levelDropCount}
) b2
on b.doctorId=b2.doctor_id group by b.drugUserId ) b3 where b3.num=#{maxLevelDropCount}

select distinct t.num, t.doctorName from ( select ifnull(sum(vdci.call_time),0) as num,d.name doctorName from virtual_doctor_call_info vdci inner join virtual_doctor_call_info_details vdcid on vdci.id=vdcid.call_id inner join doctor d on vdci.virtual_doctor_id = d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where vdci.product_id=#{productId} and vdcid.status_name='answer' and du.leader_path like #{leaderPath}
and date_format(vdci.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and WEEKOFYEAR(vdci.create_time)=WEEKOFYEAR(NOW())
and MONTH(vdci.create_time)=MONTH(NOW())
and QUARTER(vdci.create_time)=QUARTER(NOW())
and vdci.virtual_drug_user_id in ( select distinct du.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where dud.prod_id=#{productId} and du.leader_path like #{leaderPath} ) group by vdci.virtual_doctor_id ) t where t.num = #{minCallTotalTime}

select distinct a.num, a.doctorName from( select (min(t.sum_call_time)/(if(ifnull(t.call_count,0)=0,1,t.call_count))) as num,t.doctorName from ( select ifnull(sum(vdci.call_time),0) as sum_call_time,count(distinct vdci.id) as call_count,d.id as doctorId, du.name doctorName from virtual_doctor_call_info vdci inner join virtual_doctor_call_info_details vdcid on vdci.id=vdcid.call_id inner join doctor d on vdci.virtual_doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where vdci.product_id=#{productId} and vdcid.status_name='answer' and du.leader_path like #{leaderPath}
and date_format(vdci.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and WEEKOFYEAR(vdci.create_time)=WEEKOFYEAR(NOW())
and MONTH(vdci.create_time)=MONTH(NOW())
and QUARTER(vdci.create_time)=QUARTER(NOW())
and vdci.virtual_drug_user_id in ( select distinct du.id from doctor d inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where dud.prod_id=#{productId} and du.leader_path like #{leaderPath} ) group by vdci.virtual_doctor_id ) t group by t.doctorId ) a where a.num=#{minAvgCallTotalTime}

        select distinct t.num,t.doctorName from (
        select ifnull(count(vei.id),0) as num,d.name as doctorName
        from virtual_email_info vei
        inner join doctor d
        on vei.doctor_id=d.id
				inner join drug_user_doctor dud
				on d.id=dud.doctor_id
				inner join drug_user du
				on dud.drug_user_id=du.id
        where vei.product_id= #{productId}
        and du.leader_path like #{leaderPath}


                and WEEKOFYEAR(vei.create_time)=WEEKOFYEAR(NOW())


        and vei.drug_user_id in (
            select distinct du.id from doctor d
            inner join drug_user_doctor dud
            on d.id=dud.doctor_id
          inner join drug_user du
          on dud.drug_user_id= du.id
          where dud.prod_id=#{productId}
          and du.leader_path like #{leaderPath}
        )
        group by vei.doctor_id
        ) t where t.num=#{minEmailCount}

select distinct t.num,t.doctorName from ( select ifnull(sum(tmad.attend_sum_time),0) num, d.name as doctorName from t_meeting_attend_details tmad inner join t_meeting_data tma on tmad.meeting_id=tma.id inner join doctor d on tmad.doctor_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where tma.product_id=#{productId} and dud.prod_id=#{productId} and du.leader_path like #{leaderPath}
and date_format(tmad.attend_end_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and WEEKOFYEAR(tmad.attend_end_time)=WEEKOFYEAR(NOW())
and MONTH(tmad.attend_end_time)=MONTH(NOW())
and QUARTER(tmad.attend_end_time)=QUARTER(NOW())
group by tmad.doctor_id ) t where t.num=#{minMeetingTime}

select ( select ifnull(sum(t.read_time),0) from ( select distinct ar.id, ar.read_time,ar.share_type from activity_read ar inner join activity_info ai on ar.data_id=ai.id inner join t_content_product tcp on tcp.content_id=ai.id inner join product_line pl on tcp.product_id=pl.prod_id inner join doctor d on ar.read_person_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where pl.prod_id=#{productId} and dud.prod_id=#{productId} and du.leader_path like #{leaderPath}
and date_format(ar.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and WEEKOFYEAR(ar.create_time)=WEEKOFYEAR(NOW())
and MONTH(ar.create_time)=MONTH(NOW())
and QUARTER(ar.create_time)=QUARTER(NOW())
) t where t.share_type=1 ) as wechatTime , ( select ifnull(sum(t.read_time),0) from ( select distinct ar.id, ar.read_time,ar.share_type from activity_read ar inner join activity_info ai on ar.data_id=ai.id inner join t_content_product tcp on tcp.content_id=ai.id inner join product_line pl on tcp.product_id=pl.prod_id inner join doctor d on ar.read_person_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where pl.prod_id=#{productId} and dud.prod_id=#{productId} and du.leader_path like #{leaderPath}
and date_format(ar.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and WEEKOFYEAR(ar.create_time)=WEEKOFYEAR(NOW())
and MONTH(ar.create_time)=MONTH(NOW())
and QUARTER(ar.create_time)=QUARTER(NOW())
) t where t.share_type=2 ) as imTime , ( select ifnull(sum(t.read_time),0) from ( select distinct ar.id, ar.read_time,ar.share_type from activity_read ar inner join activity_info ai on ar.data_id=ai.id inner join t_content_product tcp on tcp.content_id=ai.id inner join product_line pl on tcp.product_id=pl.prod_id inner join doctor d on ar.read_person_id=d.id inner join drug_user_doctor dud on d.id=dud.doctor_id inner join drug_user du on dud.drug_user_id=du.id where pl.prod_id=#{productId} and dud.prod_id=#{productId} and du.leader_path like #{leaderPath}
and date_format(ar.create_time,'%Y-%m-%d')=date_format(NOW(),'%Y-%m-%d')
and WEEKOFYEAR(ar.create_time)=WEEKOFYEAR(NOW())
and MONTH(ar.create_time)=MONTH(NOW())
and QUARTER(ar.create_time)=QUARTER(NOW())
) t where t.share_type=3 ) as emailTime
